import{p as s,u as l,c as a,o as r,j as e,k as o,e as u,F as d,a4 as h}from"./chunks/framework.PtwvCvEk.js";const g=["markdown-url"],c={"data-allow-mismatch":"",style:{display:"none"}},f=JSON.parse('{"title":"Heat Risk Maps","description":"","frontmatter":{"layout":"page"},"headers":[],"params":{"narrative":"heat_risk_maps","file":"https://gtif-austria.github.io/public-narratives/heat_risk_maps.md","title":"Heat Risk Maps"},"relativePath":"narratives/heat_risk_maps.md","filePath":"narratives/[narrative].md"}'),m={name:"narratives/heat_risk_maps.md"},w=Object.assign(m,{setup(b){const t=s(!0);t.value=!1;const{params:i}=l();return(p,n)=>(r(),a("div",null,[e("client-only",null,[e("eox-storytelling",{"show-nav":"","markdown-url":o(i).file,class:"full-width",style:{transform:"translateY(calc(var(--vp-nav-height) - 2px))","margin-top":"calc(var(--vp-nav-height) * -1 - 90px + 48px)","margin-bottom":"var(--vp-nav-height)","--eox-storytelling-hero-height":"48dvh"}},null,8,g)]),e("div",c,[t.value?(r(),a(d,{key:0},[n[0]||(n[0]=h(' --- cover-image: https://raw.githubusercontent.com/Itsman-AT/public-narratives/Itsman-AT/heat-risk-according-to-climate-scenarios/assets/Itsman-AT/ExampleViennaRiskbuildings-1742554563509.png --- <h1 id="heat-risk-maps" tabindex="-1">Heat Risk Maps <a class="header-anchor" href="#heat-risk-maps" aria-label="Permalink to &quot;Heat Risk Maps&quot;">​</a></h1><h2 id="map-tour" tabindex="-1">Map Tour <a class="header-anchor" href="#map-tour" aria-label="Permalink to &quot;Map Tour &lt;!--{ as=&quot;eox-map&quot; mode=&quot;tour&quot; }--&gt;&quot;">​</a></h2><h3 id="" tabindex="-1"><a class="header-anchor" href="#" aria-label="Permalink to &quot;&lt;!--{ layers=&#39;[{&quot;type&quot;:&quot;Tile&quot;,&quot;properties&quot;:{&quot;id&quot;:&quot;osm&quot;},&quot;source&quot;:{&quot;type&quot;:&quot;OSM&quot;}}]&#39; center=[16.37349215538594,48.208361321627784] zoom=&quot;11&quot; animationOptions=&quot;{duration:500}&quot; }--&gt;&quot;">​</a></h3><h4 id="problemstellung-ausgangslage" tabindex="-1">Problemstellung &amp; Ausgangslage <a class="header-anchor" href="#problemstellung-ausgangslage" aria-label="Permalink to &quot;Problemstellung &amp; Ausgangslage&quot;">​</a></h4><p>Extreme Hitze untertags und keine Abkühlung in der Nacht: In Wien steigt die Anzahl an Hitzetagen und Tropennächten kontinuierlich an. <img src="https://raw.githubusercontent.com/GTIF-Austria/public-narratives/b0c30d850f866cbd8366d0e4639978a62e4079bb/assets/KlimaindizesWien-1742456280058.png" style="width:100%;height:100%;" data-fallback-src="https://raw.githubusercontent.com/Itsman-AT/public-narratives/Itsman-AT/heat-risk-according-to-climate-scenarios/assets/Itsman-AT/KlimaindizesWien-1742456280058.png"></p><p><em>Quelle: Klimastatusbericht Österreich 2023, Klimarückblick Wien, Hrsg. CCCA 2024.</em></p><h3 id="-1" tabindex="-1"><a class="header-anchor" href="#-1" aria-label="Permalink to &quot;&quot;">​</a></h3><p>Im Jahr <strong>2024</strong> gab es an der <strong>Station Hohe Warte in Wien</strong></p><ul><li>doppelt so viele Sommertage,</li><li>mehr als vier mal so viele Hitzetage, und</li><li>mehr als zehn mal so viele Tropennächte</li></ul><p>als im Referenzzeitraum 1961 - 1990. <img src="https://raw.githubusercontent.com/GTIF-Austria/public-narratives/46bd082b05871481e50abb046467285554293b3a/assets/KlimadashboardatHoheWarte2024-1747648429811.PNG" style="width:100%;height:100%;" data-fallback-src="https://raw.githubusercontent.com/schneidermartin/public-narratives/schneidermartin/feedback-implementation/assets/schneidermartin/KlimadashboardatHoheWarte2024-1747648429811.PNG"></p><p><em>Quelle: www.klimadashboard.at</em></p><h3 id="-2" tabindex="-1"><a class="header-anchor" href="#-2" aria-label="Permalink to &quot;&quot;">​</a></h3><p>Die <strong>hohe Bevölkerungsdichte</strong> und der <strong>urbane Wärmeinseleffekt</strong> zwingen Städte dazu, sich anzupassen und Maßnahmen zu ergreifen, um die zunehmende Bedrohung zu bekämpfen. Insbesondere <strong>vulnerable Gruppen</strong> leiden unter anhaltend hohen Temperaturen und Hitzestress - mit ernstzunehmenden Konsequenzen auf die Gesundheit.</p><p><strong>Doch Anpassungsmaßnahmen sind oft nur lokal wirksam, und die Mittel sind begrenzt</strong>.</p><h3 id="-3" tabindex="-1"><a class="header-anchor" href="#-3" aria-label="Permalink to &quot;&quot;">​</a></h3><p>Das wirft eine Vielzahl an <strong>drängenden Fragestellungen</strong> auf, u.a.</p><ul><li>Wo sind Maßnahmen zur Reduktion der Hitzebelastung nötig?</li><li>Welche Personengruppen sollen durch gezielte Maßnahmen besser geschützt/unterstützt werden?</li><li>Wo herrscht der dringendste Handlungsbedarf?</li><li>Welche Gebiete sollen priorisiert behandelt werden?</li><li>...</li></ul><h3 id="-4" tabindex="-1"><a class="header-anchor" href="#-4" aria-label="Permalink to &quot;&quot;">​</a></h3><p>In den letzten Jahren wurden <strong>verschiedene Untersuchungen zur Analyse des Stadtklimas und der Hitzebetroffenheit</strong> durchgeführt. Exemplarisch wird hier auf die <strong>Wiener Stadtklimaanalyse (2020)</strong> und den <strong>Urban Heat Vulnerability Index (UHVI) Wien (2019)</strong> verwiesen. <img src="https://raw.githubusercontent.com/GTIF-Austria/public-narratives/a6074f597bee163dce11a0de69ac2bc71f1f4add/assets/stadtklimaanalyse-karte-001-1742459897452.png" style="width:100%;height:100%;" data-fallback-src="https://raw.githubusercontent.com/Itsman-AT/public-narratives/Itsman-AT/heat-risk-according-to-climate-scenarios/assets/Itsman-AT/stadtklimaanalyse-karte-001-1742459897452.png"></p><p><em>Quelle: Stadtklimaanalyse Wien, Klimaanalysekarte, 2020, Stadt Wien - Stadtentwicklung und Stadtplanung, Weatherpark GmbH, INKEK GmbH</em></p><img src="https://raw.githubusercontent.com/GTIF-Austria/public-narratives/52ac3fd29e2d4aaba2255e88cc426362c13f02ce/assets/UHVI-1742459936283.PNG" style="width:100%;height:30%;" data-fallback-src="https://raw.githubusercontent.com/Itsman-AT/public-narratives/Itsman-AT/heat-risk-according-to-climate-scenarios/assets/Itsman-AT/UHVI-1742459936283.PNG"><p><em>Quelle: Darstellung von orf.at auf Basis der Daten von Stadt Wien (energie.wien.gv.at), ECOTEN, OpenStreetMap</em></p><h3 id="wo-gibt-es-verbesserungsbedarf-in-der-bereitstellung-verwendbarer-daten-und-informationen" tabindex="-1">Wo gibt es Verbesserungsbedarf in der Bereitstellung verwendbarer Daten und Informationen? <a class="header-anchor" href="#wo-gibt-es-verbesserungsbedarf-in-der-bereitstellung-verwendbarer-daten-und-informationen" aria-label="Permalink to &quot;Wo gibt es Verbesserungsbedarf in der Bereitstellung verwendbarer Daten und Informationen?&quot;">​</a></h3><h3 id="_1-flexibilitat-aktualitat" tabindex="-1">1. Flexibilität &amp; Aktualität <a class="header-anchor" href="#_1-flexibilitat-aktualitat" aria-label="Permalink to &quot;1. Flexibilität &amp; Aktualität&quot;">​</a></h3><h4 id="hitze-hitzerisiko" tabindex="-1">Hitze ≠ Hitzerisiko <a class="header-anchor" href="#hitze-hitzerisiko" aria-label="Permalink to &quot;Hitze ≠ Hitzerisiko&quot;">​</a></h4><p>Während die einen auch bei 30°C gerne in der Sonne sitzen, stellt dies für andere bereits ein gesundheitliches Risiko dar. <strong>Einmalig berechnete, statische Karten</strong> bieten eine solide Grundlage, können allerdings zu <strong>weiteren Fragen und benötigten Untersuchungen</strong> führen, die nicht unmittelbar verfügbar sind. <strong>Aktualisierungen</strong> aufgrund veränderter Ausgangslage (Landnutzung &amp; Bebauung, Bevölkerungsstruktur, o.ä.) oder variable räumliche Auflösung sind nicht möglich.</p><h3 id="_2-konsistenz" tabindex="-1">2. Konsistenz <a class="header-anchor" href="#_2-konsistenz" aria-label="Permalink to &quot;2. Konsistenz&quot;">​</a></h3><p>Durch die <strong>Verwendung unterschiedlicher Eingangsdaten, Berechnungsmethoden oder verschiedener Zielsetzungen</strong>, können inhaltsverwandte Untersuchungen Inkonsistenzen aufweisen (Ausprägung von Hitzebelastung, Handlungsnotwendigkeiten, o.ä.) und zu Entscheidungs- und Kommunikationsproblemen führen.</p><h3 id="_3-ubertragbarkeit" tabindex="-1">3. Übertragbarkeit <a class="header-anchor" href="#_3-ubertragbarkeit" aria-label="Permalink to &quot;3. Übertragbarkeit&quot;">​</a></h3><p>Detaillierte Untersuchungen erfordern meist <strong>maßgeschneiderte Lösungen für einzelne Städte</strong>. Bisher besteht noch keine einheitliche Berechnungsmethodik des Hitzerisikos, um dieses für viele Städte und Regionen zur Verfügung zu stellen.</p><h2 id="losungsweg-heat-risk-maps" tabindex="-1">Lösungsweg &quot;Heat Risk Maps&quot; <a class="header-anchor" href="#losungsweg-heat-risk-maps" aria-label="Permalink to &quot;Lösungsweg &quot;Heat Risk Maps&quot;&quot;">​</a></h2><p><strong>Flexible, konsistente und übertragbare Hitzerisikokarten</strong> sind nicht nur eine Hilfestellung für <strong>Städte und Gemeinden</strong>, sondern auch für <strong>Unternehmen und Organisationen</strong>, die künftige Hitzerisiken abschätzen müssen.</p><p>Entsprechend des Risikokonzepts des IPCC (6.Sachstandsbericht (AR6) der Arbeitsgruppe 2) setzt sich ein <strong>Risiko aus Gefahr, Exposition und Vulnerabilität</strong> zusammen.</p><img src="https://raw.githubusercontent.com/GTIF-Austria/public-narratives/4988d1aeb84df8b73bdb6692cd30f5c547074cc4/assets/Risikopropeller-1742468288131.png" style="width:100%;height:60%;" data-fallback-src="https://raw.githubusercontent.com/Itsman-AT/public-narratives/Itsman-AT/heat-risk-according-to-climate-scenarios/assets/Itsman-AT/Risikopropeller-1742468288131.png"><p>Durch eine <strong>fundierte Risikobewertung</strong> können zukünftige Entwicklungen gestaltet, wirkungsvolle Investitionen getätigt und <strong>Maßnahmen dort platziert, wo sie benötigt werden</strong>. Dafür müssen verschiedene Daten aus unterschiedlichen Disziplinen umfassend zusammengestellt und miteinander verknüpft werden.</p><p>Der entwickelte Hitzerisiko-Algorithmus kombiniert <strong>aktuelle Satelliteninformationen</strong>, wie Gebäudestruktur, Bodenversiegelungsgrad oder Baumbestand, mit <strong>Bevölkerungsstatistiken</strong> sowie hochauflösenden, auf Österreich zugeschnittenen <strong>Klimadaten</strong>.</p><img src="https://raw.githubusercontent.com/GTIF-Austria/public-narratives/f670fdd9c7b0dc24e6be13c01ac46e97d6480ccd/assets/Datasourcessketch2-1747647762985.png" data-fallback-src="https://raw.githubusercontent.com/schneidermartin/public-narratives/schneidermartin/feedback-implementation/assets/schneidermartin/Datasourcessketch2-1747647762985.png"><p>Die Methodik erlaubt es, die Hitzerisikokarten wiederholt zu berechnen, wenn neue oder aktualisierte Datensätze verfügbar sind, und macht so die <strong>Auswirkungen von Anpassungsmaßnahmen und Stadtentwicklung sichtbar</strong>.</p><p>Sie bietet auch die Möglichkeit, <strong>künftige Raumplanungsszenarien oder demografische Entwicklungen</strong> einzubeziehen und künftige Auswirkungen zu antizipieren. Während die Verwendung von open-source Daten die Grundlage bildet, können verfügbare stadtspezifische Daten eine höhere Qualität ermöglichen.</p><img src="https://raw.githubusercontent.com/GTIF-Austria/public-narratives/3fccfa1e48b774193885976c416e71b33919e68e/assets/ExampleViennaSat-1742554565946.png" data-fallback-src="https://raw.githubusercontent.com/Itsman-AT/public-narratives/Itsman-AT/heat-risk-according-to-climate-scenarios/assets/Itsman-AT/ExampleViennaSat-1742554565946.png"><img src="https://raw.githubusercontent.com/GTIF-Austria/public-narratives/214fb85ff18ec9bcf27740f14a7e955aaa138ed0/assets/ExampleViennaRiskbuildings-1742554563509.png" data-fallback-src="https://raw.githubusercontent.com/Itsman-AT/public-narratives/Itsman-AT/heat-risk-according-to-climate-scenarios/assets/Itsman-AT/ExampleViennaRiskbuildings-1742554563509.png"><p>Die Hitzerisikokarten ermöglichen somit, die <strong>räumliche Verteilung des Hitzerisikos</strong> für eine <strong>definierte Bevölkerungsgruppe, Infrastruktur, o.ä.</strong> unter <strong>gewählten klimatischen Bedingungen</strong> zu ermitteln.</p><p><strong>Beispiele: Auswahl möglicher Hitzegefahr</strong>:</p><ul><li>Tagesmaximum der Lufttemperatur &gt; 25°C / 30°C / 35°C / 40°C</li><li>Tagesminimum der Lufttemperatur &gt; 20°C</li><li>X aufeinanderfolgende Tage mit Tagesmaximum der Lufttemperatur &gt; Y°C</li><li>unter gegenwärtigen, oder unter zukünftigen Klimabedingungen</li><li>... weitere Klimaindizes</li></ul><p><strong>Beispiele: Definition vulnerabler Gruppen</strong>:</p><ul><li>Personen jünger als 14 Jahre</li><li>Personen älter als 65 Jahre</li><li>Standorte vulnerabler, sozialer Infrastruktur (Kindergärten, Krankenhäuser, Pflegeheime, o.ä.)</li><li>Menschen mit geringem Einkommen</li><li>Berücksichtigung vorhandener Cool Spots o.ä. kühlen Aufenthaltsmöglichkeiten</li><li>... weitere Faktoren zur Steigerung/Reduktion der Vulnerabilität</li></ul><h2 id="anwendungsfalle" tabindex="-1">Anwendungsfälle <a class="header-anchor" href="#anwendungsfalle" aria-label="Permalink to &quot;Anwendungsfälle&quot;">​</a></h2><p>Durch die Flexibilität der Datenauswahl, kann das Hitzerisiko für eine Vielzahl von Anwendungsfällen berechnet werden, u.a.</p><ul><li><strong>Hitzeaktions- und Hitzeschutzpläne</strong>: Die Integration von Hitzerisikokarten kann helfen, Maßnahmen auf die gefährdeten Gebiete zuzuschneiden.</li><li><strong>Stadtentwicklungskonzepte</strong>: Hitzerisikokarten liefern wertvolle Informationen für Raumplanung und Stadtentwicklung, um ihre Stadt für die Zukunft lebenswert zu gestalten.</li><li><strong>Priorisierung von Anpassungsmaßnahmen</strong>: Vergleich von Gebieten innerhalb von Städten und Regionen.</li><li><strong>Aktualisierung aktueller Informationen zum Hitzerisiko</strong>: Sich ändernde Klimabedingungen, die Entwicklung von Stadtgebieten und veränderte Bevölkerungsstatistiken machen veraltete Karten unbrauchbar. Der entwickelte Algorithmus ermöglicht unkomplizierte Aktualisierungen.</li><li><strong>Auffinden von Orten mit zukünftigen Risiken</strong>: Organisationen und Unternehmen, die mit vulnerablen Gruppen zu tun haben, können Orte mit hohem oder niedrigem, aktuellen und künftigen Hitzerisiko ermitteln, was für die Planung von Standorten und Einsätzen entscheidend ist.</li></ul><h2 id="qualitatskontrolle" tabindex="-1">Qualitätskontrolle <a class="header-anchor" href="#qualitatskontrolle" aria-label="Permalink to &quot;Qualitätskontrolle&quot;">​</a></h2><p>Die <strong>Aktualität der statischen Eingangsdaten</strong> stellt eine wesentliche Qualitätskomponente dar. Durch eine <strong>Trendanalyse der Oberflächentemperatur</strong> (OHB), können Veränderungen in der Landnutzungs- und Bebauungsstruktur einer Stadt identifiziert werden. An diesen Orten ist die Aktualität der Eingangsdaten zu überprüfen.</p><h3 id="hochaufgeloste-oberflachentemperatur" tabindex="-1">Hochaufgelöste Oberflächentemperatur <a class="header-anchor" href="#hochaufgeloste-oberflachentemperatur" aria-label="Permalink to &quot;Hochaufgelöste Oberflächentemperatur&quot;">​</a></h3><img src="https://raw.githubusercontent.com/GTIF-Austria/public-narratives/1161fc4afe6b170fb033c111d7688186563af72f/assets/LST-1742473151887.PNG" style="width:40%;height:40%;" data-fallback-src="https://raw.githubusercontent.com/Itsman-AT/public-narratives/Itsman-AT/heat-risk-according-to-climate-scenarios/assets/Itsman-AT/LST-1742473151887.PNG"><p>Quelle: OHB SE (<a href="https://www.ohb.de/" target="_blank" rel="noreferrer">https://www.ohb.de/</a>)</p><ul><li></li></ul><h3 id="trendanalyse-der-oberflachentemperatur" tabindex="-1">Trendanalyse der Oberflächentemperatur <a class="header-anchor" href="#trendanalyse-der-oberflachentemperatur" aria-label="Permalink to &quot;Trendanalyse der Oberflächentemperatur&quot;">​</a></h3><img src="https://raw.githubusercontent.com/GTIF-Austria/public-narratives/a6890d9562a2c1bba56e136177bff4cfec398cc2/assets/OHB-1742553790083.png" data-fallback-src="https://raw.githubusercontent.com/Itsman-AT/public-narratives/Itsman-AT/heat-risk-according-to-climate-scenarios/assets/Itsman-AT/OHB-1742553790083.png"><p>Quelle: OHB SE (<a href="https://www.ohb.de/" target="_blank" rel="noreferrer">https://www.ohb.de/</a>)</p><h1 id="kontakt" tabindex="-1">Kontakt <a class="header-anchor" href="#kontakt" aria-label="Permalink to &quot;Kontakt&quot;">​</a></h1><p><strong>Jan Peters-Anders</strong><br> Research Engineer<br> Digital Resilient Cities<br> Center for Energy</p><p><strong>AIT Austrian Institute of Technology GmbH</strong><br> Giefinggasse 6 | 1210 Vienna | Austria<br> T +43 50550-4586 | M +43 664 8157995<br><a href="mailto:jan.peters-anders@ait.ac.at" target="_blank" rel="noreferrer">jan.peters-anders@ait.ac.at</a> | www.ait.ac.at</p>',62))],64)):u("",!0)])]))}});export{f as __pageData,w as default};
